var authToken = msg.req.headers['x-auth-token']; //es un atributo de un objeto en js osea msg.req.headers.x-auth-token donde esta el token de autenticacion
msg.auth = (authToken && authToken == 'clave');     //que tenga un valor y que ese valor sea la palabra 'clave'
if(!msg.auth){
    msg.statusCode=401; //prohibido, configuracion de respuesta
}
msg.cabeceras=[];

//recorro todas las claves de headers y por cada clave le voy agregando al arreglo "cabecera" el nombre de la clave y el valor de la clave
Object.keys(msg.req.headers).forEach(function(o,i){
    msg.cabeceras.push({name:o,value:msg.req.headers[o]});
});
return msg;

//recorro cada cookie y todo lo que empieza con nombre demo se le asigno un null, asi se borran las cookies
var cookieNames = Object.keys(msg.req.cookies).filter(function(cookieName){return /^demo-/.test(cookieName);})
msg.cookies = {};

cookieNames.forEach(function(cookieName){
    msg.cookies[cookieName] = null;
});

